import React, { useState } from "react";
import { FiEdit3, FiUser, FiLink } from "react-icons/fi";
import { FaFacebook, FaTwitter, FaInstagram } from "react-icons/fa";
import "./ContactPage.css";

const Contact = () => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: ""
  });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");

    try {
      const res = await fetch("http://localhost:5000/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });

      const data = await res.json();

      if (data.success) {
        setStatus("✅ Message sent successfully!");
        setFormData({ name: "", email: "", message: "" });
      } else {
        setStatus("❌ Failed to send message. Try again.");
      }
    } catch (error) {
      console.error(error);
      setStatus("❌ Server error. Please try again later.");
    }
  };

  return (
    <div className="contact-page">
      <h1 className="contact-title">Contact Me</h1>
      <div className="contact-cards">
        {/* Card 1 - Get in Touch */}
        <div className="contact-card">
          <div className="card-icon">
            <FiEdit3 size={32} />
          </div>
          <h2>Get in Touch</h2>
          <p>Fill out the form with your name, email, and message to connect!</p>
          {!showForm ? (
            <button
              className="contact-link"
              onClick={() => setShowForm(true)}
            >
              <b>Show contact form</b>
            </button>
          ) : (
            <>
              <form className="contact-form" onSubmit={handleSubmit}>
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />

                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />

                <label>Message</label>
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  required
                ></textarea>

                <button type="submit" className="send-btn">
                  Send Message
                </button>
              </form>
              {status && <p className="status-msg">{status}</p>}
              <button
                className="contact-link"
                onClick={() => setShowForm(false)}
              >
                <b>Hide contact form</b>
              </button>
            </>
          )}
        </div>

        {/* Card 2 - Direct Contact */}
        <div className="contact-card">
          <div className="card-icon">
            <FiUser size={32} />
          </div>
          <h2>Direct Contact</h2>
          <p>
            Email:{" "}
            <a
              href="mailto:vedikavakhare@gmail.com"
              className="direct-link"
            >
              vedikavakhare@gmail.com
            </a>
          </p>
          <p>
            Phone:{" "}
            <a href="tel:+19016505424" className="direct-link">
              (901) 650-5424
            </a>
          </p>
        </div>

        {/* Card 3 - Follow Me */}
        <div className="contact-card">
          <div className="card-icon">
            <FiLink size={32} />
          </div>
          <h2>Follow Me</h2>
          <ul className="social-icons">
            <li>
              <a
                href="https://www.facebook.com/"
                target="_blank"
                rel="noopener noreferrer"
                className="sociallink"
              >
                <FaFacebook size={24} /> Facebook
              </a>
            </li>
            <li>
              <a
                href="https://twitter.com"
                target="_blank"
                rel="noopener noreferrer"
                className="sociallink"
              >
                <FaTwitter size={24} /> Twitter
              </a>
            </li>
            <li>
              <a
                href="https://www.instagram.com/"
                target="_blank"
                rel="noopener noreferrer"
                className="sociallink"
              >
                <FaInstagram size={24} /> Instagram
              </a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Contact;
