import React, { useState } from 'react';
import './ContactPage.css';

function ContactPage() {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [response, setResponse] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResponse('Sending...');
    try {
      const res = await fetch("/api/contact", {  // ‚úÖ No localhost hardcoding
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });

      if (!res.ok) {
        throw new Error('Failed to send message');
      }

      const data = await res.json();
      setResponse(data.message || "Message sent successfully!");
      setForm({ name: '', email: '', message: '' });
    } catch (err) {
      console.error(err);
      setResponse('‚ùå Something went wrong. Please try again.');
    }
  };

  return (
    <div className="contact-container">
      <h2>üì© Contact Me</h2>
      <div className="contact-box">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            required
            value={form.name}
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Your Email"
            required
            value={form.email}
            onChange={handleChange}
          />
          <textarea
            name="message"
            placeholder="Your Message"
            required
            value={form.message}
            onChange={handleChange}
          ></textarea>
          <button type="submit">Send Message</button>
          <p className="response">{response}</p>
        </form>

        <div className="contact-info">
          <h3>üìû Contact Info</h3>
          <p><strong>Email:</strong> vedika.yourname@gmail.com</p>
          <p><strong>Phone:</strong> +91 98765 43210</p>
          <p><strong>Location:</strong> Maharashtra, India</p>
        </div>
      </div>
    </div>
  );
}

export default ContactPage;
