// src/aiChatService.js
import axios from 'axios';

const API_URL = "http://localhost:5000/api/chat"; // Replace with your backend API

export const sendMessageToAI = async (message) => {
  try {
    const response = await axios.post(API_URL, { message });
    return response.data.reply; // Assume backend returns { reply: "AI response" }
  } catch (error) {
    console.error("AI API Error:", error);
    return "ðŸ˜… Sorry, I couldn't process that. Try again!";
  }
};
