import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ServiceCards.css';

const ServiceCards = () => {
  const navigate = useNavigate();

  const services = [
    {
      id: 1,
      icon: 'ðŸ‘—',
      title: 'Fashion Suggestion',
      description: 'What should i wear today?',
      path: '/fashion-suggestion'
    },
    {
      id: 2,
      icon: 'ðŸ§˜â€â™€',
      title: 'Health Tips',
      description: 'Tips to stay fit and strong',
      path: '/health-tips'
    },
    {
      id: 3,
      icon: 'ðŸ“…',
      title: 'Daily Routine',
      description: 'Plan your perfect day',
      path: '/daily-routine'
    },
    {
      id: 4,
      icon: 'ðŸ’†â€â™€',
      title: 'Skin & Hair Care',
      description: 'Your daily skin & hair routine',
      path: '/skin-hair-care'
    },
    {
      id: 5,
      icon: 'âœ…',
      title: 'To-do List',
      description: 'Track your daily goals',
      path: '/todo-list'
    },
    {
      id: 6,
      icon: 'â°',
      title: 'Reminders',
      description: "Don't forget your self-care",
      path: '/reminders'
    }
  ];

  const handleCardClick = (path) => {
    navigate(path);
  };

  return (
    <section className="service-cards">
      <div className="cards-grid">
        {services.map((service) => (
          <div 
            key={service.id} 
            className="service-card"
            onClick={() => handleCardClick(service.path)}
          >
            <div className="card-icon">
              {service.icon}
            </div>
            <h3 className="card-title">{service.title}</h3>
            <p className="card-description">{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ServiceCards;
